import './App.css';
import React,{useState} from 'react'
import Table from './component/Table.js'
import AddWork from './component/Add';

function App() {
  const [works,setworks]=useState([{work:"Go to school", status:1},{work:"Get up", status:0}]);
  const [addButton,setaddButton]=useState(false);

  /*function componentWillMount(){
    if(localStorage && localStorage.getItem('works')){
        var works=JSON.parse(localStorage.getItem('works'));
        setworks(works);
    }
    console.log('none');
  }*/

  function setAddForm()
  {
    setaddButton(!addButton);
  }

  function handleSubmit(work,status,e)
  {
    const New={work:work,status:status};  
    works.push(New);
    setworks(works);
    /*setaddButton(!addButton);*/
    localStorage.setItem('works', JSON.stringify(works));
  }


  function deleteItem(item,e)
  {
    const NewValue=works;
    NewValue.splice(item,1);
    setworks(NewValue);
  }
  return (
    <div className="container">
      <div className="row">
        <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
          <h1 className="text-center text-success">ToDo App</h1>
        </div>
      </div>
      <div className="row">
        {addButton&&

        <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4 col-xl-4 add">
          <button type="button" className="closeAdd btn bg-transparent" onClick={setAddForm}>    
            <i className="fas fa-window-close addIcon"></i>
          </button> 
          <AddWork change={handleSubmit}/>
        </div>
          }
        <div className={addButton?"col-xs-8 col-sm-8 col-md-8 col-lg-8 col-xl-8":"col-xs-12 col-sm-12 col-md-12 col-lg-12 col-xl-12"}>
          {!addButton &&
            <button type="button" className="btn btn-info btn-sm" onClick={setAddForm}>
              Thêm công việc
            </button>
          }
          <Table work={works} index={deleteItem}/>
        </div>
      </div>
    </div>
  );
}

export default App;
