import React from 'react'

const AddWork=(props)=>{
    function handleSubmit(event){
        props.change(work,status);
    }
    var work="";
    var status=1;
    function handleChange(event){
        const target=event.target;
        const name=target.name;
        const value=target.value;
        
        name==='work'? work=value:status=value;
    }
    return (
        <form>
            <label>Công việc:</label>
            <input 
                type="text" 
                className="form-control" 
                name="work" 
                placeholder="Hãy nhập vào công việc"
                onChange={handleChange}
            />
            <label>Trạng thái</label>
            <select 
                className="form-control" 
                name="status"
                onChange={handleChange}>
              <option value={1}>Quan trọng</option>
              <option value={0}>Không quan trọng</option>
            </select>
            <button type="button" className="btn btn-primary mt-100" onClick={handleSubmit}>Lưu</button>
        </form>
    )
}
export default AddWork
