import './App.css';
import React,{useState} from 'react'
import Table from './component/Table.js'

function App() {
  const [works,setworks]=useState([{work:"Go to school", status:1},{work:"Get up", status:0}]);
  const [addButton,setaddButton]=useState(false);

  function setAddForm()
  {
    setaddButton(!addButton);
  }
  return (
    <div className="container">
      <div className="row">
        <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
          <h1 className="text-center text-success">ToDo App</h1>
        </div>
      </div>
      <div className="row">
        {addButton&&

        <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4 col-xl-4 add">
          <button type="button" className="closeAdd btn bg-transparent" onClick={setAddForm}>    
            <i className="fas fa-window-close addIcon"></i>
          </button> 
          <form>
            <label>Công việc:</label>
            <input type="text" className="form-control" name="work" placeholder="Hãy nhập vào công việc"/>
            <label>Trạng thái</label>
            <select className="form-control" name="status">
              <option value={1}>Quan trọng</option>
              <option value={0}>Không quan trọng</option>
            </select>
            <button type="submit" className="btn btn-primary mt-100">Lưu</button>
          </form>
        </div>
          }
        <div className={addButton?"col-xs-8 col-sm-8 col-md-8 col-lg-8 col-xl-8":"col-xs-12 col-sm-12 col-md-12 col-lg-12 col-xl-12"}>
          {!addButton &&
            <button type="button" className="btn btn-info  btn-sm" onClick={setAddForm}>
              Thêm công việc
            </button>
          }
          <Table/>
        </div>
      </div>
    </div>
  );
}

export default App;
